import React, { useState, useEffect } from 'react';
import './LogsExplorer.css';

const LogsExplorer = ({ setCurrentPage }) => {
  const [logs, setLogs] = useState([]);
  const [filteredLogs, setFilteredLogs] = useState([]);
  const [filterType, setFilterType] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOrder, setSortOrder] = useState('newest');

  // Simulate getting logs from localStorage or context
  useEffect(() => {
    // In a real app, you'd get this from a global state or localStorage
    const mockLogs = [
      {
        id: 1,
        timestamp: '14:23:15',
        fullTimestamp: '2024-01-15T14:23:15.123Z',
        message: '🚀 Random resource allocation simulation started',
        type: 'system',
        details: null
      },
      {
        id: 2,
        timestamp: '14:23:17',
        fullTimestamp: '2024-01-15T14:23:17.456Z',
        message: '🔄 Resource DB (Database) randomly allocated to Database Process (P1)',
        type: 'allocation',
        details: {
          resource: 'DB',
          process: 'P1',
          allocationId: 'alloc_001'
        }
      },
      {
        id: 3,
        timestamp: '14:23:20',
        fullTimestamp: '2024-01-15T14:23:20.789Z',
        message: '☠️ DEADLOCK DETECTED: circular-wait between processes P1, P3',
        type: 'deadlock',
        details: {
          type: 'circular-wait',
          processes: ['P1', 'P3'],
          resources: ['DB', 'NET'],
          conditions: {
            mutualExclusion: true,
            holdAndWait: true,
            noPreemption: true,
            circularWait: true
          }
        }
      },
      {
        id: 4,
        timestamp: '14:23:22',
        fullTimestamp: '2024-01-15T14:23:22.012Z',
        message: '🔍 Deadlock Conditions Met: Mutual Exclusion ✓, Hold & Wait ✓, No Preemption ✓, Circular Wait ✓',
        type: 'deadlock-analysis',
        details: null
      },
      {
        id: 5,
        timestamp: '14:23:25',
        fullTimestamp: '2024-01-15T14:23:25.345Z',
        message: '⚡ Attempting to resolve deadlocks...',
        type: 'resolve',
        details: null
      },
      {
        id: 6,
        timestamp: '14:23:27',
        fullTimestamp: '2024-01-15T14:23:27.678Z',
        message: '✅ Deadlock resolution completed - resources preempted',
        type: 'success',
        details: {
          resolvedProcesses: ['P1', 'P3'],
          preemptedResources: ['DB']
        }
      }
    ];
    
    setLogs(mockLogs);
    setFilteredLogs(mockLogs);
  }, []);

  useEffect(() => {
    let filtered = logs;
    
    // Filter by type
    if (filterType !== 'all') {
      filtered = filtered.filter(log => log.type === filterType);
    }
    
    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(log => 
        log.message.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    
    // Sort
    filtered = [...filtered].sort((a, b) => {
      const dateA = new Date(a.fullTimestamp);
      const dateB = new Date(b.fullTimestamp);
      return sortOrder === 'newest' ? dateB - dateA : dateA - dateB;
    });
    
    setFilteredLogs(filtered);
  }, [logs, filterType, searchTerm, sortOrder]);

  const getLogTypeColor = (type) => {
    const colors = {
      system: '#17a2b8',
      allocation: '#28a745',
      deadlock: '#dc3545',
      'deadlock-analysis': '#fd7e14',
      warning: '#ffc107',
      error: '#dc3545',
      success: '#28a745',
      scan: '#6f42c1',
      resolve: '#007bff',
      info: '#6c757d'
    };
    return colors[type] || '#6c757d';
  };

  const formatFullTimestamp = (timestamp) => {
    return new Date(timestamp).toLocaleString();
  };

  const exportLogs = () => {
    const logData = filteredLogs.map(log => ({
      timestamp: formatFullTimestamp(log.fullTimestamp),
      type: log.type,
      message: log.message,
      details: log.details ? JSON.stringify(log.details) : 'N/A'
    }));
    
    const csvContent = [
      'Timestamp,Type,Message,Details',
      ...logData.map(log => 
        `"${log.timestamp}","${log.type}","${log.message}","${log.details}"`
      )
    ].join('\n');
    
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `system-logs-${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="logs-explorer-container">
      <div className="logs-explorer-header">
        <div className="header-top">
          <button 
            className="back-btn"
            onClick={() => setCurrentPage('Simulation')}
          >
            ← Back to Simulation
          </button>
          <h1>📋 System Logs Explorer</h1>
          <button className="export-btn" onClick={exportLogs}>
            📥 Export CSV
          </button>
        </div>
        
        <div className="filters-section">
          <div className="filter-group">
            <label>Filter by Type:</label>
            <select 
              value={filterType} 
              onChange={(e) => setFilterType(e.target.value)}
              className="filter-select"
            >
              <option value="all">All Types</option>
              <option value="system">System</option>
              <option value="allocation">Allocation</option>
              <option value="deadlock">Deadlock</option>
              <option value="deadlock-analysis">Deadlock Analysis</option>
              <option value="warning">Warning</option>
              <option value="error">Error</option>
              <option value="success">Success</option>
              <option value="scan">Scan</option>
              <option value="resolve">Resolve</option>
            </select>
          </div>
          
          <div className="filter-group">
            <label>Search:</label>
            <input 
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Search logs..."
              className="search-input"
            />
          </div>
          
          <div className="filter-group">
            <label>Sort:</label>
            <select 
              value={sortOrder} 
              onChange={(e) => setSortOrder(e.target.value)}
              className="filter-select"
            >
              <option value="newest">Newest First</option>
              <option value="oldest">Oldest First</option>
            </select>
          </div>
        </div>
      </div>
      
      <div className="logs-stats">
        <div className="stat-item">
          <span className="stat-label">Total Logs:</span>
          <span className="stat-value">{logs.length}</span>
        </div>
        <div className="stat-item">
          <span className="stat-label">Filtered:</span>
          <span className="stat-value">{filteredLogs.length}</span>
        </div>
        <div className="stat-item">
          <span className="stat-label">Deadlocks:</span>
          <span className="stat-value stat-value--danger">
            {logs.filter(log => log.type === 'deadlock').length}
          </span>
        </div>
      </div>
      
      <div className="logs-table-container">
        <table className="logs-table">
          <thead>
            <tr>
              <th>Timestamp</th>
              <th>Type</th>
              <th>Message</th>
              <th>Details</th>
            </tr>
          </thead>
          <tbody>
            {filteredLogs.map((log) => (
              <tr key={log.id} className={`log-row log-row--${log.type}`}>
                <td className="timestamp-cell">
                  <div className="timestamp-short">{log.timestamp}</div>
                  <div className="timestamp-full">{formatFullTimestamp(log.fullTimestamp)}</div>
                </td>
                <td className="type-cell">
                  <span 
                    className="type-badge"
                    style={{ backgroundColor: getLogTypeColor(log.type) }}
                  >
                    {log.type.toUpperCase()}
                  </span>
                </td>
                <td className="message-cell">{log.message}</td>
                <td className="details-cell">
                  {log.details ? (
                    <details className="details-expandable">
                      <summary>View Details</summary>
                      <pre className="details-content">
                        {JSON.stringify(log.details, null, 2)}
                      </pre>
                    </details>
                  ) : (
                    <span className="no-details">—</span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        
        {filteredLogs.length === 0 && (
          <div className="no-logs">
            <div className="no-logs-icon">📝</div>
            <div className="no-logs-message">No logs match your current filters</div>
          </div>
        )}
      </div>
    </div>
  );
};

export default LogsExplorer;
