import React, { useState } from 'react';
import './ResourceExplorer.css';

const ResourceExplorer = ({ setCurrentPage }) => {
  const [selectedResourceType, setSelectedResourceType] = useState(null);

  const resourceTypes = [
    {
      id: 'cpu',
      name: 'CPU Resources',
      icon: 'fas fa-microchip',
      description: 'CPU allocation and scheduling',
      color: '#e74c3c'
    },
    {
      id: 'memory',
      name: 'Memory Resources',
      icon: 'fas fa-memory',
      description: 'RAM and virtual memory',
      color: '#3498db'
    },
    {
      id: 'disk',
      name: 'Disk I/O Resources',
      icon: 'fas fa-hdd',
      description: 'Storage and file operations',
      color: '#f39c12'
    },
    {
      id: 'network',
      name: 'Network Resources',
      icon: 'fas fa-network-wired',
      description: 'Bandwidth and connections',
      color: '#2ecc71'
    },
    {
      id: 'semaphore',
      name: 'Semaphores',
      icon: 'fas fa-lock',
      description: 'Synchronization primitives',
      color: '#9b59b6'
    },
    {
      id: 'mutex',
      name: 'Mutex Locks',
      icon: 'fas fa-key',
      description: 'Mutual exclusion locks',
      color: '#e67e22'
    }
  ];

  const handleBackToSimulation = () => {
    setCurrentPage('Simulation');
  };

  return (
    <div className="resource-explorer-container">
      <div className="explorer-header">
        <button className="back-btn" onClick={handleBackToSimulation}>
          <i className="fas fa-arrow-left"></i>
          Back to Simulation
        </button>
        <h1>Resource Type Explorer</h1>
        <p>Explore different types of system resources and their deadlock characteristics</p>
      </div>

      <div className="resource-grid">
        {resourceTypes.map((resource) => (
          <div 
            key={resource.id} 
            className={`resource-card ${selectedResourceType === resource.id ? 'selected' : ''}`}
            onClick={() => setSelectedResourceType(resource.id)}
            style={{ borderColor: resource.color }}
          >
            <div className="resource-icon" style={{ color: resource.color }}>
              <i className={resource.icon}></i>
            </div>
            <h3>{resource.name}</h3>
            <p>{resource.description}</p>
          </div>
        ))}
      </div>

      {selectedResourceType && (
        <div className="resource-details-panel">
          <h2>Resource Analysis</h2>
          <div className="analysis-content">
            <div className="deadlock-scenarios">
              <h3>Deadlock Scenarios</h3>
              <ul>
                <li>Circular wait conditions</li>
                <li>Hold and wait situations</li>
                <li>No preemption conflicts</li>
              </ul>
            </div>
            <div className="prevention-strategies">
              <h3>Prevention Strategies</h3>
              <ul>
                <li>Resource ordering protocols</li>
                <li>Timeout mechanisms</li>
                <li>Preemption policies</li>
              </ul>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ResourceExplorer;
